    onPcqtChange: function(field, newValue, oldValue, eOpts) {
        //alert(newValue);
        if(newValue=='正在进行')
        {//alert();
            var cjstore=Ext.StoreMgr.get('cjjl');
            var jgstore=Ext.StoreMgr.get('ssjg');
            var n=cjstore.count();

            runner.stopAll();
            var pcqt=Ext.getCmp('pcqt');
            var num=10;
            var jxdjs =runner.newTask({
                run: function()
                {
                    //alert();
                    if(pcqt.getValue()!='0')
                    {
                        pcqt.setValue(num);
                        num=num-1;
                    }
                    else
                    {//alert();
                        runner.stopAll();
                        var btn1=Ext.getCmp('surebtn');
                        var btn2=Ext.getCmp('resetbtn');
                        //btn1.disable();
                       // btn2.disable();
                        pcqt.setValue('结束');
                    }
                },
                interval:1000
            }
            );
            jxdjs.start();

            var cjjl =runner.newTask({
                run: function()
                {
                    cjstore.load({
                        params:{zbpcbh:Ext.getCmp('jpzbpcbh').getValue()},
                        callback:function(){
                            if(n!=cjstore.count())
                            {
                                n=cjstore.count();
                                num=10;
                            }
                            var d = Ext.getDom('cc');

                            d.scrollTop = d.scrollHeight - d.offsetHeight;
                        }
                    }
                    );



                },
                interval:500
            }
            );
            cjjl.start();

            var ssjg =runner.newTask({
                run: function()
                {
                    jgstore.load({
                        params:{zbpcbh:Ext.getCmp('jpzbpcbh').getValue()}
                    }
                    );

                },
                interval:500
            }
            );
            ssjg.start();
        }

        if(newValue===0)
        {alert('结束');
            runner.stopAll();
            var btn1=Ext.getCmp('surebtn');
            var btn2=Ext.getCmp('resetbtn');
            btn1.disable();
            btn2.disable();
        }
    },

